INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Wacker Drive', 'A major multilevel 2.2 mile stretch in lower Chicago, running along the south side of the main branch and the east side of the south branch of the Chicago River in the Loop.', '233 E Wacker Dr, Chicago, IL 60601', '314-651-9791');
