INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Chicago Board of Trade', 'The Chicago Board of Trade building is an iconic Art Deco skyscraper on West Jackson Boulevard. In film, it stood in as Bruce Wayne’s corporate HQ in Batman Begins. One of its trading floors was shown in Ferris Buellers day Off, also featured in several scenes in The Untouchables.', '141 W Jackson Blvd, Chicago, IL 60604', '650-993-7074');
INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Art Institute of Chicago', 'Ferris and his friends take on the streets of Chicago, with a drop-by at the Art Institute of Chicago.', '111 S Michigan Ave, Chicago, IL 60603', '715-663-5265');
INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Union Station', 'As one of Chicago’s most iconic and historical landmarks, Union Station serves as backdrop to various scenes in movie history. It’s the citys primary terminal for commuter trains, covering over 9 city blocks underneath Chicago’s network of streets and skyscrapers.', '225 S Canal St, Chicago, IL 60606', '314-651-9791');
INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Wrigley Field', 'Wrigley Field is promently featured in several films, such as Ferris Beullers Day Off, A League of Their Own, and Rookie of the Year', '1060 W Addison St, Chicago, IL 60613', '523-760-6681');
INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('The Fireside Bowl', ' The retro-styled bowling alley has not only hosted serious bowlers for ages, it was also the nexus of Chicago’s punk scene for many years. It is featured in The Break Up', 'S2646 W Fullerton Ave, Chicago, IL 60647', '650-993-7074');
