INSERT INTO "Locations" ("Name", "Description", "Address", "Telephone") VALUES ('Times Square', 'Featured in numerous films, Taxi Driver, Midnight Cowboy, Date Night, Captain America, Big, Shaft, A Chorus Line, Vanilla Sky, Birdman… ', 'Manhattan, NY 10036', '523-760-6681');
